import './css/LoginPage.css';
import { Link } from 'react-router-dom';
import React from 'react';
import axios from 'axios';

class LoginPage extends React.Component {

   constructor(){
        super()
        this.state = {
            logemail: "",
            logpassword: ""
        }
        this.handleChange = this.handleChange.bind(this)
        this.login = this.login.bind(this)
    }

    handleChange(event){
        const {name, value} = event.target
        this.setState({[name] : value})
    }

    login(event){
        event.preventDefault()
        const loggedIn = {

            email : this.state.logemail,
            password : this.state.logpassword
        }

        axios.post('http://localhost:4000/app/login', loggedIn)
            .then(response => console.log(response.data))

        axios.get('http://localhost:4000/app/posts')
            .then(response => console.log(response.data) )
    }

    render(){
        return (

        <div class="container">
            <div class="welcome-div">
                <h1>Welcome, back !</h1>
            </div>

            <div class="form-div">
                <form onsubmit="">
                    <div class="logintitle">
                        <h1>Login</h1>
                        <hr id="loginhr"/>
                    </div>

                    <div class="labels-inputs">
                        <div class="input-box">

                            <label>Email</label>
                            <input class="box" type="email" name="logemail" placeholder=" âœ‰ï¸Enter Email Id" onChange={this.handleChange} value = {this.state.logemail}/>

                        </div>

                        <div class="input-box">
                            <label>Password</label>
                            <input class="box" type="password" name="logpassword" placeholder=" ðŸ”’Enter Password" onChange={this.handleChange} value = {this.state.logpassword}/>
                        </div>
                    </div>

                    <div><p>Forgot Password?</p></div>

                    <div class="signin-button">
                        <button onClick={this.login}>Sign In</button>
                    </div>

                    <hr id="sign-in-up"/>

                    <div class="signup-button">
                        <Link to="/signup">
                            <button id="createbutton">Create an Account</button>
                        </Link>

                    </div>

                </form>
            </div>
        </div>


  );
    }
}

export default LoginPage;
